import React from "react";

const ChartBar = (props) => {
  let barfillHeight = "0%";
  if (props.maxValue > 0) {
    barfillHeight = Math.round((props.value / props.maxValue) * 100) + "%";
  }
  return (
    <div className="h-full flex flex-col items-center">
      <div className="h-full w-full border-1 border-[#313131 rounded-xl bg-[#c3b4f3] overflow-hidden flex-col justify-end">
        <div
          style={{ height: barfillHeight }}
          className="bg=[#4836b9] w-full ease-in transform transition-all duration-300"
        ></div>
      </div>
      <div className="font-bold text-xs text-center">{props.label}</div>
    </div>
  );
};
export default ChartBar;
