import React, { useState } from "react";
import ExpenseDate from "./ExpenseDate";
import Card from "../ui/Card";

const ExpenseItem = (props) => {
  const [title, setTitle] = useState(props.title);

  const clickHandler = () => {
    setTitle("Updated!");
    console.log(title);
  };
  return (
    <Card className="flex justify-between items-center p-2 m-4 bg-slate-400 ">
      <ExpenseDate date={props.date} />
      <div className="flex flex-col-reverse gap-1 items-end justify-start md:flex-row md:items-center md:justify-start md:flex-1">
        <h2 className=" text-red-900 text-xl flex-1 md:mx-4 mx-1 md:text-2xl">
          {title}
        </h2>
        <div
          type="button"
          className="bg-red-400 text-gray-50 font-bold p-2 rounded-lg border-2 border-white md:text-xl md:py-2 md:px-6 hover:cursor-pointer"
        >
          {props.amount} RUP
        </div>
      </div>
      <button
        onClick={clickHandler}
        className="px-8 py-3 bg-gradient-to-r from-red-800 to-red-500 ml-10 mr-4 rounded-lg shadow-xl text-white hover:opacity-70 "
      >
        Change Title!
      </button>
    </Card>
  );
};

export default ExpenseItem;
